static void ast_printBinary(string_buffer_Buf* out, uint64_t value)
{
   char tmp[64];
   tmp[63] = 0;
   char* cp = &tmp[62];
   while (value) {
      *cp = '0' + ((value & 0x1));
      cp--;
      value /= 2;
   }
   *cp-- = 'b';
   *cp = '0';
   string_buffer_Buf_add(out, cp);
}

static void ast_printOctal(string_buffer_Buf* out, uint64_t value)
{
   char tmp[32];
   tmp[31] = 0;
   char* cp = &tmp[30];
   while (value) {
      *cp = '0' + ((value & 0x7));
      cp--;
      value /= 8;
   }
   *cp = '0';
   string_buffer_Buf_add(out, cp);
}

