   e->num_args = ((uint8_t)(num_args));
   e->fn = fn;
   memcpy(((void*)(e->args)), ((void*)(args)), num_args * sizeof(ast_Expr*));
   ast_TypeRef* destRef = ((ast_TypeRef*)(&e->args[num_args]));
   ast_TypeRefHolder_fill(ref, destRef);
