   uint32_t size = sizeof(ast_CallExpr) + e->num_args * sizeof(ast_Expr*);
   ast_CallExpr* e2 = ast_context_Context_alloc(inst->c, size);
   e2->parent = e->parent;
   e2->endLoc = e->endLoc;
   e2->num_args = e->num_args;
