      break;
case ast_BuiltinExprKind_Elemsof:
      break;
case ast_BuiltinExprKind_EnumMin:
      break;
case ast_BuiltinExprKind_EnumMax:
      break;
case ast_BuiltinExprKind_OffsetOf:
