case 2:
         string_buffer_Buf_add1(out, 'w');
         string_buffer_Buf_print(start, "\t%%.%u =l alloc4 2\n", next_idx);
         string_buffer_Buf_print(start, "\tstoreh %%.%u, %%.%u\n", idx, next_idx);
