   ast_QualType rtype = module_analyser_Analyser_analyseExpr(ma, ast_BinaryOperator_getRHS2(b), true, module_analyser_RHS);
   if (ast_QualType_isInvalid(&rtype)) return ast_QualType_Invalid;

