      break;
case ast_BinaryOpcode_Assign: {
      bool ok = conversion_checker_Checker_check(&ma->checker, ltype, rtype, ast_BinaryOperator_getRHS2(b), ast_Expr_getLoc(e));
      if (ok) result = ltype;
      break;
   }
case ast_BinaryOpcode_MulAssign:
